function arraySum(numArray){console.log('arraySum');var result=0;for(var i=0;i<numArray.length;i++){result=result+numArray[i];}return result;}function weightedRandom(values,weights){console.log('weightedRandom');var sum=arraySum(weights);var rand=Math.floor(Math.random()*sum)+1;var i=0;var n=0;while(i<values.length){n=n+weights[i];if(n>=rand){break;}i++;}return values[i];}var values=['Första','Andra','Tredje','Fjärde'];var weights=[40,30,20,10];var res=weightedRandom(values,weights);console.log(res);